import React from 'react';
import './App.css';
import SubscribeForm from './SubscribeForm';
import AccountPage from './AccountPage';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            user: {
                id: Date.now(),
                userName: '',
                email: '',
                password: '',
                subscribed: false
            }
        }
    }

    createNewUser = (newUser) => {
        console.log(newUser[0]);
        this.setState({ user: newUser });
        localStorage.setItem("userId", this.state.user.id);
        this.saveUserToDb(newUser[0]);
        // this.checkIfLoggedIn();
    }

    saveUserToDb = (user) => {
        fetch('http://localhost:4000/users', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(user),
        })
            .then(response => response.json())
            .then(data => {
                console.log('Success:', data);
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    }

    getUser() {
        fetch('http://localhost:4000/users')
            .then(response => response.json())
            .then((data) => {
                console.log(data);
                var person = data.find(x => x.id === localStorage.getItem("userId"));
                this.setState({ user: person });
            });
    }

    checkIfLoggedIn = () => {
        if (localStorage.getItem("userId") !== null) {
            return (<AccountPage userName={this.state.user.name} />);
        }
        else {
            return (<SubscribeForm postUser={this.createNewUser} />);
        }
    }

    render() {
        return (
            <div>
                {this.checkIfLoggedIn()}
            </div>
        );
    }

}

export default App;
