import React, { Component } from 'react'
import SubscribeBtn from './SubscribeBtn';

export class AccountPage extends Component {
    constructor(props) {
        super(props);
        this.state = { id: null, userName: '', subscribed: null, text: '' }
    }

    componentDidMount = () => {
        fetch('http://localhost:4000/users')
            .then(response => response.json())
            .then((data) => {
                console.log(data);
                console.log(localStorage.getItem("userId"));
                var person = data.find(x => x.id.toString() === localStorage.getItem("userId"));
                this.setState({ id: person.id })
                this.setState({ userName: person.userName });
                this.setState({ subscribed: person.subscribed })
                this.checkIfSubbed();
            });
    }

    checkIfSubbed = () => {
        if (!this.state.subscribed) {
            this.setState({ text: 'Subscribe' })
        }
        else if (this.state.subscribed) {
            this.setState({ text: 'Unsubscribe' })
        }
        else {
            console.error("sub is null")
        }
    }


    render() {
        return (
            <div>
                <h3>Welcome {this.state.userName}</h3>
                <SubscribeBtn text={this.state.text} />
            </div>
        )
    }
}

export default AccountPage
