import React from 'react';
import './App.css';
import SubscribeForm from './SubscribeForm';
import AccountPage from './AccountPage';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            user: {
                id: Date.now(),
                name: '',
                email: '',
                password: '',
                subscribed: false
            },
            retData: ''
        }
    }

    createNewUser = (newUser) => {
        console.log(newUser);
        localStorage.setItem("userId", this.state.user.id);
    }

    checkIfLoggedIn = () => {
        if (localStorage.getItem("userId") !== null) {
            this.state.retData = (<AccountPage />)
            return retData;
        }
        else {
            retData = (<SubscribeForm postUser={this.createNewUser} />)
            return retData
        }
    }

    render() {
        var test;
        if (localStorage.getItem("userId" !== null)) {
            test = (<AccountPage />)
        }
        else {
            test = (<SubscribeForm postUser={this.createNewUser} />)
        }
        return (
            <div>
                {test}
            </div>
        );
    }

}

export default App;
